const routes = {
  getComponents: done => {
    done(null, require("app/_client/views/App"))
  },
  childRoutes: [
    {
      path: "/",
      onEnter: (nextState, transition) => {
        transition.to("/report")
      }
    },
    {
      path: "/demo",
      getComponents: done => {
        done(null, require("app/report/_client/views/Demo"))
      }
    },
    {
      path: "/report",
      getComponents: done => {
        done(null, require("app/report/_client/views/Examples"))
      }
    },
    {
      path: "/report/:id",
      getComponents: done => {
        done(null, require("app/report/_client/views/Report"))
      }
    }
  ]
}

export default routes
