import React from "react"
import Space from "app/_client/views/Space"

const {Component, PropTypes} = React

class Collapse extends Component {

  static propTypes = {
    id: PropTypes.string.isRequired,
    label: PropTypes.string.isRequired,
    icon: PropTypes.string.isRequired,
    children: PropTypes.node.isRequired
  }

  render() {
    const {id, label, icon, children, ...others} = this.props
    return <div {...others}>
      <button className="btn btn-primary" type="button" data-toggle="collapse" data-target={"#" + id}
          aria-expanded="false" aria-controls={id}>
        <i className={"fa fa-fw fa-" + icon}></i> {label}
      </button>
      <div className="collapse in" id={id}>
        <Space/>
        {children}
      </div>
      <Space/>
    </div>
  }

}

export default Collapse
