import {flattenRoutes, convertClientRoutes} from "app/_shared/utils/route"
import {renderReact} from "app/_server/handlers"

const routes = flattenRoutes([
  {
    path: "/favicon.ico",
    handler: {file: "public/favicon.ico"},
    config: {cache: {expiresIn: 86400000, privacy: "public"}}
  },
  {
    path: "/public/{path*}",
    handler: {directory: {path: "public"}}
  },
  convertClientRoutes(require("app/_client/routes"), renderReact)
])

export default routes
