import _ from "lodash"
import {renderReact} from "app/_server/handlers"
import {getBlockData} from "app/demo/_server/handlers"

const routes = [
  {
    path: "/public/{path*}",
    handler: {
      directory: {
        path: "public"
      }
    }
  },
  {
    path: "/demo",
    handler: renderReact
  },
  {
    path: "/api/block/{id}",
    handler: getBlockData
  }
].map(route => _.defaults(route, {
  method: "GET"
}))

export default routes
