import {getAllReports, getReport} from "app/api/handlers/report"

export function register(server, config, next) {
  server.route([
    {
      method: "GET",
      path: "/report",
      handler: getAllReports
    },
    {
      method: "GET",
      path: "/report/{id}",
      handler: getReport
    }
  ])
  next()
}

register.attributes = {
  name: "agridash-api"
}
