import {getReport, getReportViewData} from "app/api/handlers/report"

export function register(server, config, next) {
  server.route([
    {
      method: "GET",
      path: "/report/{id}",
      handler: getReport
    },
    {
      method: "GET",
      path: "/report/{id}/view/{viewId}/data",
      handler: getReportViewData
    }
  ])
  next()
}

register.attributes = {
  name: "agridash-api"
}
