import {register} from "app/block/_client/widget"

register({
  bar: require("app/block/_client/views/widgets/Bar"),
  pie: require("app/block/_client/views/widgets/Pie"),
  line: require("app/block/_client/views/widgets/Line"),
  area: require("app/block/_client/views/widgets/Area")
})

const routes = {
  getComponents: done => done(null, require("app/block/_client/views/BlockList")),
  childRoutes: [
    {
      path: ":id",
      getComponents: done => done(null, require("app/block/_client/views/Block"))
    }
  ]
}

export default routes
