export function getDefaultOptions(widget, block) {
  return {
    title: {
      text: block.title
    },
    credits: false,
    xAxis: {
      title: false
    },
    yAxis: {
      title: false
    },
    plotOptions: {
      series: {
        animation: {
          duration: 250
        }
      }
    }
  }
}

export function drawChart(container, options) {
  const {$} = window
  $(container).highcharts(options)
}
