import Chance from "chance"
import {range} from "app/_shared/utils"

const chance = new Chance(123456)

function mock(loader) {
  const {timeRange, fields} = loader
  if (timeRange) {
    return Promise.resolve(range(timeRange[0], timeRange[1] + 1).map(time => {
      const record = {
        time
      }
      fields.forEach(field => {
        record[field.id] = "" + chance.natural({
          min: 10000,
          max: 1000000
        })
      })
      return record
    }))
  }
}

export default mock
