import {getAllBlocks, getBlock, getBlockData} from "app/block/_server/handlers"
import {register} from "app/block/_server/loader"

register({
  "mock": require("app/block/_server/loaders/mock")
})

const routes = {
  handler: getAllBlocks,
  childRoutes: [
    {
      path: "{id}",
      handler: getBlock
    },
    {
      path: "{id}/data",
      handler: getBlockData
    }
  ]
}

export default routes
