import {getAllBlocks, getBlock, getBlockData} from "app/block/_server/handlers"
import {register} from "app/block/_server/loader"

register({
  "groupByState": require("app/block/_server/loaders/groupByState"),
  "groupByYear": require("app/block/_server/loaders/groupByYear"),
  "nass/getCommodityValue": require("app/block/_server/loaders/nass/getCommodityValue")
})

const routes = {
  handler: getAllBlocks,
  childRoutes: [
    {
      path: "{id}",
      handler: getBlock
    },
    {
      path: "{id}/data",
      handler: getBlockData
    }
  ]
}

export default routes
