import _ from "lodash"
import sql from "app/utils/sql"

export function findByParams(params) {
  _.defaults(params, {
    domainid: 341,
    countryid: 1, agglevelid: 7,
    frequencyid: 1, referenceperioddesc: "YEAR"
  })
  return sql.select("stateid", "value").from("nasscommodityvalue").where(params)
}
