import React from "react"
import {get} from "app/utils/ajax"
import Link from "app/_client/views/Link"
import Block from "app/report/_client/views/Block"

const {Component} = React

class Demo extends Component {

  componentDidMount() {
    get("/api/block").then(blocks => {
      this.setState({
        blocks
      })
    })
  }

  state = {}

  render() {
    const {blocks} = this.state
    return <div>
      <ul className="nav">
        <li className="nav-item">
          <Link to="/">Home</Link>
        </li>
      </ul>
      {blocks && <div className="container">
        <div className="row">
          {blocks.map(block => <div key={block.id} className="col-md-6">
            <Block block={block}/>
          </div>)}
        </div>
      </div>}
    </div>
  }

}

export default Demo
