import React from "react"
import {findDOMNode} from "react-dom"
import styles from "app/report/_client/views/Map.css"

const {Component} = React

class Report extends Component {

  componentDidMount() {
    const {L} = window
    const map = L.map(findDOMNode(this), {
      attributionControl: false,
      zoomAnimation: false,
      center: [37.8, -96.9],
      zoom: 4
    })
    // L.tileLayer("https://{s}.tiles.mapbox.com/v4/{style}/{z}/{x}/{y}.png?access_token={key}", {
    //   style: "mapbox.light",
    //   key: "pk.eyJ1IjoiYWdyaWRhc2giLCJhIjoiYjdiZjc4YTA0ZjhhODg0N2VkMDM0ODQwNGUwNDA0ZDQifQ.jmfdrMQc252iW2ovBPRTOg"
    // }).addTo(map)
    L.tileLayer("http://localhost:7007/area/{z}/{x}/{y}.png").addTo(map)
  }

  render() {
    return <div className={styles.container}>
    </div>
  }

}

export default Report
