import React from "react"
import {get} from "app/utils/ajax"
import Link from "app/_client/views/Link"
import Block from "app/report/_client/views/Block"

const {Component, PropTypes} = React

class Report extends Component {

  static propTypes = {
    params: PropTypes.object.isRequired
  }

  componentDidMount() {
    const {params} = this.props
    get("/api/block/" + params.id).then(block => {
      this.setState({
        block
      })
    })
  }

  state = {}

  render() {
    const {block} = this.state
    return <div>
      <ul className="nav">
        <li className="nav-item">
          <Link to="/">Home</Link>
        </li>
      </ul>
      {block && <div className="container">
        <Block block={block}/>
      </div>}
    </div>
  }

}

export default Report
