import * as area from "app/tile/layers/area"
import * as bubble from "app/tile/layers/bubble"

export function register(server, config, next) {
  server.route([
    {
      method: "GET",
      path: "/area/{z}/{x}/{y}.png",
      handler: area.renderImage
    },
    {
      method: "GET",
      path: "/bubble/{z}/{x}/{y}.png",
      handler: bubble.renderImage
    }
  ])
  next()
}

register.attributes = {
  name: "agridash-tile"
}
