import renderImage from "app/tile/handlers/renderImage"
import {handlePromise} from "app/core/utils/handler"

export function register(server, config, next) {
  server.route([
    {
      method: "GET",
      path: "/report/{id}/view/{viewId}/{z}/{x}/{y}.png",
      handler: handlePromise(renderImage)
    }
  ])
  next()
}

register.attributes = {
  name: "agridash-tile"
}
