import path from "path"
import fs from "fs"

export const name = "area"

export const mapStyles = fs.readFileSync(path.join(__dirname, "area.xml"), "utf8")

export function getDataSource() {
  return {
    table: "geo",
    column: "geom",
    query: "select geom, id % 4 + 1 as level from geo where source = 'US_COUNTY'",
    styles: ["area"]
  }
}
