const areaLayer = {
  name: "area",
  getDataOptions: request => ({
    query: `select geom, id % 4 + 1 as level from geo where source = '${request.query.s}'`,
    style: `<Style name="area">
      <Rule>
        <Filter>[level] = 1</Filter>
        <PolygonSymbolizer fill="#bae4b3" fill-opacity="0.8"/>
      </Rule>
      <Rule>
        <Filter>[level] = 2</Filter>
        <PolygonSymbolizer fill="#74c476" fill-opacity="0.8"/>
      </Rule>
      <Rule>
        <Filter>[level] = 3</Filter>
        <PolygonSymbolizer fill="#31a354" fill-opacity="0.8"/>
      </Rule>
      <Rule>
        <Filter>[level] = 4</Filter>
        <PolygonSymbolizer fill="#006d2c" fill-opacity="0.8"/>
      </Rule>
    </Style>`
  })
}

export default areaLayer
