import path from "path"
import fs from "fs"

export const name = "bubble"

export const mapStyles = fs.readFileSync(path.join(__dirname, "bubble.xml"), "utf8")

export const bufferSize = 128

export function getDataSource() {
  return {
    table: "geo",
    column: "pos",
    query: "select pos, (properties->>'aland')::bigint % 4 + 1 as level from geo",
    styles: ["bubble"]
  }
}
