const columnLayer = {
  name: "column",
  bufferSize: 128,
  getDataOptions: request => ({
    table: "geo",
    column: "col",
    query: `select col, (id * 5) % 4 + 1 as level from geo where source = '${request.query.s}'`,
    style: `<Style name="column">
      <Rule>
        <Filter>[level] = 1</Filter>
        <BuildingSymbolizer fill="#ffff00" height="100000" fill-opacity="0.8"/>
      </Rule>
      <Rule>
        <Filter>[level] = 2</Filter>
        <BuildingSymbolizer fill="#ffff00" height="200000" fill-opacity="0.8"/>
      </Rule>
      <Rule>
        <Filter>[level] = 3</Filter>
        <BuildingSymbolizer fill="#ffff00" height="400000" fill-opacity="0.8"/>
      </Rule>
      <Rule>
        <Filter>[level] = 4</Filter>
        <BuildingSymbolizer fill="#ffff00" height="800000" fill-opacity="0.8"/>
      </Rule>
    </Style>`
  })
}

export default columnLayer
