function dot(options) {
  return {
    bufferSize: 16,
    query: `
      select pos as geom from geo where source = '${options.source}' and (properties->>'aland')::bigint > 10000000000
      order by lat desc, lng desc
    `,
    style: `<Style name="${options.styleName}">
      <Rule>
        <MarkersSymbolizer allow-overlap="true" fill="#000000" width="5" height="5"/>
      </Rule>
    </Style>`
  }
}

export default dot
