import path from "path"
import fs from "fs"

export const name = "dot"

export const mapStyles = fs.readFileSync(path.join(__dirname, "dot.xml"), "utf8")

export const bufferSize = 128

export function getDataSource() {
  return {
    table: "geo",
    column: "pos",
    query: "select pos from geo where source = 'US_COUNTY' and (properties->>'aland')::bigint < 1000000000",
    styles: ["dot"]
  }
}
