const dotLayer = {
  name: "dot",
  bufferSize: 16,
  render: request => ({
    query: `
      select pos as geom from geo where source = '${request.query.s}' and (properties->>'aland')::bigint > 10000000000
    `,
    style: `<Style name="dot">
      <Rule>
        <MarkersSymbolizer allow-overlap="true" fill="#000000" width="5" height="5"/>
      </Rule>
    </Style>`
  })
}

export default dotLayer
