const heatLayer = {
  name: "heat",
  bufferSize: 64,
  getDataOptions: request => ({
    table: "geo",
    column: "pos",
    query: `select pos from geo where source = '${request.query.s}' and (properties->>'aland')::bigint < 1000000000`,
    style: `<Style name="heat" comp-op="multiply"
        image-filters="colorize-alpha(blue, cyan, green, yellow , orange, red)">
      <Rule>
        <MarkersSymbolizer allow-overlap="true" file="images/heat.png"/>
      </Rule>
    </Style>`
  })
}

export default heatLayer
