function heat(options) {
  return {
    bufferSize: 64,
    query: `
      select pos as geom from geo where source = '${options.source}' and (properties->>'aland')::bigint < 1000000000
      order by lat desc, lng desc
    `,
    style: `<Style name="${options.styleName}" image-filters="colorize-alpha(blue, cyan, green, yellow, red)">
      <Rule>
        <MarkersSymbolizer allow-overlap="true" file="images/heat.png"/>
      </Rule>
    </Style>`
  }
}

export default heat
