export function getSchema() {
  return {
    type: "object",
    properties: {
      username: {
        type: "string",
        required: true
      },
      password: {
        type: "string",
        required: true
      }
    }
  }
}

export function getOptions(options) {
  return {
    label: "Log In",
    fields: {
      username: {
        placeholder: "Username"
      },
      password: {
        type: "password",
        placeholder: "Password"
      }
    },
    ...options
  }
}
