import {getSchema as getLayerTypeSchema, getOptions as getLayerTypeOptions} from "app/web/forms/layerType"
import {getSchema as getIdSchema} from "app/web/forms/id"
import {getOptions as getRefCategoryOptions} from "app/web/forms/refCategory"
import {getSchema as getLocationTypeSchema, getOptions as getLocationTypeOptions} from "app/web/forms/locationType"

export function getSchema() {
  return {
    type: "object",
    properties: {
      type: getLayerTypeSchema(),
      category: getIdSchema(),
      locationType: getLocationTypeSchema()
    }
  }
}

export function getOptions() {
  return {
    label: "Layer",
    fields: {
      type: getLayerTypeOptions(),
      category: getRefCategoryOptions(),
      locationType: getLocationTypeOptions()
    }
  }
}
