import {createSelectOptions} from "app/web/forms/common"

export function getSchema() {
  return {
    type: "string",
    required: true,
    default: "area"
  }
}

export function getOptions() {
  return createSelectOptions({
    label: "Type",
    dataSource: [
      {
        value: "area",
        text: "Area"
      },
      {
        value: "heat",
        text: "Heat"
      },
      {
        value: "dot",
        text: "Dot"
      },
      {
        value: "bubble",
        text: "Bubble"
      },
      {
        value: "column",
        text: "Column"
      }
    ]
  })
}
