import {createSelectOptions} from "app/web/forms/common"

export function getSchema() {
  return {
    type: "number",
    required: true,
    default: 1
  }
}

export function getOptions(options) {
  return createSelectOptions({
    label: "Mock Category",
    dataSource: [
      {
        value: 1,
        text: "A"
      },
      {
        value: 2,
        text: "B"
      },
      {
        value: 3,
        text: "C"
      },
      {
        value: 4,
        text: "D"
      },
      {
        value: 5,
        text: "E"
      }
    ],
    ...options
  })
}
