import {getSelectOptions} from "app/web/forms/common"
import {getSchema as getIdSchema} from "app/web/forms/id"

export function getSchema() {
  return {
    ...getIdSchema(),
    default: 1
  }
}

export function getOptions(options) {
  return getSelectOptions({
    label: "Mock Category",
    dataSource: [
      {
        value: 1,
        text: "A"
      },
      {
        value: 2,
        text: "B"
      },
      {
        value: 3,
        text: "C"
      },
      {
        value: 4,
        text: "D"
      },
      {
        value: 5,
        text: "E"
      }
    ],
    ...options
  })
}
