export function getSchema() {
  return {
    type: "number",
    default: 1,
    required: true
  }
}

export function getOptions() {
  return {
    type: "hidden"
  }
}

export function updateId(parentField, childField, nextIdField) {
  const id = nextIdField.getValue()
  const {id: idField} = childField.childrenByPropertyId
  idField.setValue(id)
  nextIdField.setValue(id + 1)
}
