import {getArraySchema, getMultiSelectOptions, subscribeDataSource} from "app/web/forms/common"
import {getSchema as getIdSchema} from "app/web/forms/id"

export function getSchema() {
  return getArraySchema(getIdSchema())
}

export function getOptions(options) {
  return getMultiSelectOptions({
    label: "Categories",
    ...subscribeDataSource("categories"),
    ...options
  })
}
