export function getSchema() {
  return {
    type: "object",
    properties: {
      name: {
        type: "string",
        minLength: 3,
        maxLength: 20,
        required: true
      },
      username: {
        type: "string",
        minLength: 3,
        maxLength: 20,
        required: true
      },
      password: {
        type: "string",
        minLength: 3,
        maxLength: 20,
        required: true
      }
    }
  }
}

export function getOptions(options) {
  return {
    label: "Sign Up",
    fields: {
      name: {
        label: "Name"
      },
      username: {
        label: "Username"
      },
      password: {
        type: "password",
        label: "Password"
      }
    },
    ...options
  }
}
