import createSelectOptions from "app/web/forms/createSelectOptions"

export function getSchema() {
  return {
    type: "string",
    required: true,
    default: "last10"
  }
}

export function getOptions(options) {
  return createSelectOptions({
    label: "Time Range",
    dataSource: [
      {
        value: "last5",
        text: "Last 5 Years"
      },
      {
        value: "last10",
        text: "Last 10 Years"
      },
      {
        value: "last15",
        text: "Last 15 Years"
      },
      {
        value: "last20",
        text: "Last 20 Years"
      }
    ],
    ...options
  })
}
