import {createSelectOptions} from "app/web/forms/common"

export function getSchema() {
  return {
    type: "string",
    required: true,
    default: "map"
  }
}

export function getOptions(options) {
  return createSelectOptions({
    label: "Type",
    dataSource: [
      {
        value: "map",
        text: "Map"
      },
      {
        value: "trends",
        text: "Trends"
      }
    ],
    ...options
  })
}
