const {$, L} = window

// Disable transitions on mobile devices
if (L.Browser.mobile) {
  $(document).ready(() => {
    $.support.transition = false
  })
}

// Hide navbar when clicking menu items
$(document).on("click", ".navbar-collapse.in", function(event) {
  const target = $(event.target)
  if (target.is("a") && !target.hasClass("dropdown-toggle")) {
    $(this).collapse("hide")
  }
})

// Keep dropdown when clicking non-link items
$(document).on("click", ".dropdown-menu", event => {
  const target = $(event.target)
  if (!target.is("a")) {
    event.stopPropagation()
  }
})
