import {handleActions} from "redux-actions"

import {REFRESH_DATA_SOURCE} from "app/web/actions/form"

const formReducer = handleActions({
  [REFRESH_DATA_SOURCE]: (state, action) => {
    return {
      ...state,
      dataSource: {
        ...state.dataSource,
        ...action.payload
      }
    }
  }
}, {
  dataSource: {}
})

export default formReducer
