import {handleActions} from "redux-actions"

import {REQUEST_GEOS, RECEIVE_GEOS} from "app/web/actions/geo"

const geoReducer = handleActions({
  [REQUEST_GEOS]: (state, action) => {
    return {
      ...state,
      [action.payload.source]: {
        promise: action.payload.promise
      }
    }
  },
  [RECEIVE_GEOS]: (state, action) => {
    return {
      ...state,
      [action.payload.source]: {
        promise: null,
        geos: action.payload.geos
      }
    }
  }
}, {})

export default geoReducer
