import {handleActions} from "redux-actions"

import {GEOS_REQUEST, GEOS_RECEIVE} from "app/web/actions/geos"

const geos = handleActions({
  [GEOS_REQUEST]: (state, action) => {
    return {
      ...state,
      [action.payload]: {
        isFetching: true
      }
    }
  },
  [GEOS_RECEIVE]: (state, action) => {
    return {
      ...state,
      [action.payload.source]: {
        isFetching: false,
        geos: action.payload.geos
      }
    }
  }
}, {})

export default geos
