import {handleActions} from "redux-actions"

import {UPDATE_INFO, REQUEST_LOGIN} from "app/web/actions/info"

const infoReducer = handleActions({
  [UPDATE_INFO]: (state, action) => {
    return {
      ...state,
      ...action.payload
    }
  },
  [REQUEST_LOGIN]: state => {
    return {
      ...state,
      user: {
        loading: true
      }
    }
  }
}, {})

export default infoReducer
