import {handleActions} from "redux-actions"
import {forEach} from "app/core/utils"

import {UPDATE_MODEL} from "app/web/actions/model"

const modelReducer = handleActions({
  [UPDATE_MODEL]: (state, action) => {
    const nextState = {...state}
    forEach(action.payload, (update, name) => {
      nextState[name] = {
        ...state[name],
        ...update
      }
    })
    return nextState
  }
}, {})

export default modelReducer
