import {handleActions} from "redux-actions"

import {REQUEST_NEW_REPORT, RECEIVE_NEW_REPORT} from "app/web/actions/report"

const reportReducer = handleActions({
  [REQUEST_NEW_REPORT]: (state, action) => {
    return {
      ...state,
      newReport: {
        report: action.payload,
        creating: true
      }
    }
  },
  [RECEIVE_NEW_REPORT]: (state, action) => {
    return {
      ...state,
      newReport: {
        report: action.payload,
        creating: false
      }
    }
  }
}, {})

export default reportReducer
