import React from "react"
import {connect} from "react-redux"
import {navBarContainer, searchBox} from "app/web/views/NavBar.css"
import Link from "app/web/views/Link"
import Icon from "app/web/views/Icon"

function NavBar({user}) {
  return <nav className={`navbar navbar-inverse ${navBarContainer}`}>
    <div className="container">
      <div className="navbar-header">
        <button type="button" className="navbar-toggle collapsed" data-toggle="collapse" data-target="#mainMenu">
          <span className="icon-bar"></span>
          <span className="icon-bar"></span>
          <span className="icon-bar"></span>
        </button>
        <Link to="/" className="navbar-brand">AgriDash</Link>
      </div>
      <div id="mainMenu" className="collapse navbar-collapse">
        <ul className="nav navbar-nav">
          <li className="dropdown">
            <a href="#" className="dropdown-toggle" data-toggle="dropdown">
              Reports <span className="caret"></span>
            </a>
            <ul className="dropdown-menu">
              <li>
                <div className={`input-group ${searchBox}`}>
                  <input type="text" className="form-control" placeholder="Search"/>
                  <span className="input-group-btn">
                    <button className="btn btn-default" type="button"><Icon icon="search"/></button>
                  </span>
                </div>
              </li>
              <li><Link to="/report/new">Create report</Link></li>
            </ul>
          </li>
          <li><Link to="/about">About</Link></li>
          <li><Link to="/help">Help</Link></li>
        </ul>
        <ul className="nav navbar-nav navbar-right">
          <li><a href="https://github.com/baodang/agridash" target="_blank"><Icon icon="github" fw/> GitHub</a></li>
          {!user && <li><Link to="/signup"><Icon icon="user-plus" fw/> Sign Up</Link></li>}
          {!user && <li><Link to="/login"><Icon icon="sign-in" fw/> Log In</Link></li>}
          {user && <li><Link to={`/user/${user.username}`}><Icon icon="user" fw/> {user.name}</Link></li>}
          {user && <li><Link to="/logout"><Icon icon="sign-out" fw/> Log Out</Link></li>}
        </ul>
      </div>
    </div>
  </nav>
}

export default connect(state => {
  const {info: {userId}, model: {user: userState}} = state
  const props = {}
  if (userId) {
    props.user = userState[userId]
  }
  return props
})(NavBar)
