import React from "react"
import cx from "classnames"
import styles from "app/web/views/NavBar.css"
import Link from "app/web/views/Link"
import Icon from "app/web/views/Icon"

function DropdownToggle(props) {
  return <a className="nav-link dropdown-toggle" data-toggle="dropdown" role="button" href="#" {...props}></a>
}

function DropdownItem(props) {
  return <Link className={cx("dropdown-item", styles.dropdownItem)} {...props}/>
}

function DropdownDivider() {
  return <div className={cx("dropdown-divider", styles.dropdownDivider)}></div>
}

function DropdownTitle(props) {
  return <div className={cx("text-muted", "text-uppercase", styles.dropdownItem)} {...props}></div>
}

function NavBar() {
  return <nav className="navbar navbar-static-top navbar-light">
    <Link to="/" className="navbar-brand">AgriDash</Link>
    <ul className="nav navbar-nav">
      <li className="nav-item dropdown">
        <DropdownToggle>Reports</DropdownToggle>
        <div className="dropdown-menu">
          <DropdownItem to="/report/create">
            <Icon icon="plus-circle" fw/> Create report
          </DropdownItem>
          <DropdownDivider/>
          <DropdownTitle>Recently Viewed</DropdownTitle>
          <DropdownItem to="/report/1">Demo 1</DropdownItem>
          <DropdownItem to="/report/2">Demo 2</DropdownItem>
          <DropdownItem to="/user/baodang/recently-viewed">
            <Icon icon="ellipsis-h" fw/> More
          </DropdownItem>
        </div>
      </li>
    </ul>
  </nav>
}

export default NavBar
