import React from "react"
import Link from "app/web/views/Link"

const {Component} = React

class NotFound extends Component {

  goBack = () => {
    window.history.go(-1)
    return false
  }

  render() {
    return <div className="alert alert-warning" style={{marginTop: "3rem"}} role="alert">
      <strong>Page Not Found!</strong>
      {" "}
      You can go back to the <a href="#" onClick={this.goBack}>previous page</a> or visit <Link to="/">Home</Link>.
    </div>
  }

}

export default NotFound
